const config = $('HTTP Request').first().json;
const webhook = $('Webhook').first().json.body;
const now = new Date();
const hour = now.getHours();
const day = now.getDay();

const diasLaborales = config.dias_laborales || [1, 2, 3, 4, 5];
const enDiaLaboral = diasLaborales.includes(day);
const enHorario = hour >= config.hora_inicio && hour < config.hora_fin;
const abierto = enDiaLaboral && enHorario;

const mensaje = webhook.mensaje || '';
const mensajeLimpio = mensaje.toLowerCase().trim();

const nombreSesion = config.sessionKey || 
                     webhook.sessionKey || 
                     `${webhook.empresaId}_empresa_${webhook.empresaId}`;

// PRIORIDAD 1: Verificar si hay sesi√≥n activa esperando nombre
try {
  const sesionResponse = await fetch(`http://host.docker.internal:3000/api/pedidos/sesion/${webhook.empresaId}/${webhook.numeroOrigen}`);
  const sesionData = await sesionResponse.json();

  if (sesionData.success && sesionData.data && sesionData.data.estado === 'esperando_nombre') {
    if (mensajeLimpio === 'cancelar') {
      await fetch(`http://host.docker.internal:3000/api/pedidos/sesion/${webhook.empresaId}/${webhook.numeroOrigen}`, {
        method: 'DELETE'
      });

      return {
        json: {
          ...config,
          numeroOrigen: webhook.numeroOrigen,
          empresaId: webhook.empresaId,
          nombreSesion: nombreSesion,
          pushName: webhook.pushName,
          mensaje_usuario: mensajeLimpio,
          mensaje: "‚ùå *Pedido cancelado*\n\n‚úÖ Si cambias de opini√≥n, escribe *productos* para ver el cat√°logo.",
          tipo: 'respuesta_directa',
          abierto: abierto
        }
      };
    }

    if (mensaje.trim().length < 3) {
      return {
        json: {
          ...config,
          numeroOrigen: webhook.numeroOrigen,
          empresaId: webhook.empresaId,
          nombreSesion: nombreSesion,
          pushName: webhook.pushName,
          mensaje_usuario: mensajeLimpio,
          mensaje: "‚ö†Ô∏è *Nombre muy corto*\n\nüìù Por favor escribe tu nombre completo (m√≠nimo 3 caracteres).\n\n‚ùå O escribe *cancelar* para anular.",
          tipo: 'respuesta_directa',
          abierto: abierto
        }
      };
    }

    const datosPedido = typeof sesionData.data.datos_pedido === 'string' 
      ? JSON.parse(sesionData.data.datos_pedido) 
      : sesionData.data.datos_pedido;

    return {
      json: {
        ...config,
        numeroOrigen: webhook.numeroOrigen,
        empresaId: webhook.empresaId,
        nombreSesion: nombreSesion,
        pushName: webhook.pushName,
        nombreCliente: mensaje.trim(),
        items: datosPedido.items,
        total: datosPedido.total,
        tipo: 'crear_pedido_final',
        mensaje_usuario: mensajeLimpio,
        abierto: abierto
      }
    };
  }
} catch (error) {
  console.log('‚ÑπÔ∏è No hay sesi√≥n activa');
}

// PRIORIDAD 2: Verificar si es formato de pedido
const patronPedido = /^\d+,\d+(;\d+,\d+)*$/;
if (patronPedido.test(mensajeLimpio)) {
  return {
    json: {
      ...config,
      numeroOrigen: webhook.numeroOrigen,
      empresaId: webhook.empresaId,
      nombreSesion: nombreSesion,
      pushName: webhook.pushName,
      mensaje_usuario: mensajeLimpio,
      tipo: 'formato_pedido',
      abierto: abierto,
      hora_actual: hour,
      dia_actual: day
    }
  };
}

// PRIORIDAD 3: Flujo normal
return {
  json: {
    ...config,
    mensaje_usuario: mensajeLimpio,
    numeroOrigen: webhook.numeroOrigen,
    pushName: webhook.pushName,
    empresaId: webhook.empresaId,
    nombreSesion: nombreSesion,
    conectado: config.conectado,
    abierto: abierto,
    hora_actual: hour,
    dia_actual: day,
    mensaje_respuesta: abierto ? config.mensaje_horario : config.mensaje_fuera_horario
  }
};

const httpData = $input.first().json;
const switchData = $('Switch').first().json;
const productos = httpData.data;

let nombreSesion = switchData.nombreSesion;
if (nombreSesion && nombreSesion.startsWith(`${switchData.empresaId}_`)) {
  nombreSesion = nombreSesion.substring(`${switchData.empresaId}_`.length);
}

if (!productos || productos.length === 0) {
  return {
    numeroOrigen: switchData.numeroOrigen,
    empresaId: switchData.empresaId,
    nombreSesion: nombreSesion,
    mensaje: "üì¶ No hay productos disponibles actualmente."
  };
}

// Construir TODO el cat√°logo como TEXTO con placeholders para im√°genes
let mensajeCompleto = 'üì¶ *Cat√°logo de Productos*\n\n';

// Guardar info de productos con imagen para enviarlos despu√©s
const productosConImagen = [];

productos.forEach((p, i) => {
  const numero = i + 1;

  // Agregar al texto
  mensajeCompleto += `*${numero}.* ${p.nombre_item}\n`;

  if (p.precio) {
    const precio = parseFloat(p.precio).toFixed(2);
    mensajeCompleto += `üí∞ Precio: $${precio}\n`;
  }

  if (p.descripcion) {
    mensajeCompleto += `üìù ${p.descripcion}\n`;
  }

  mensajeCompleto += '\n';

  // Guardar para enviar imagen despu√©s
  if (p.imagen_url && p.imagen_url.trim() !== '') {
    productosConImagen.push({
      numero: numero,
      nombre: p.nombre_item,
      precio: p.precio ? `$${parseFloat(p.precio).toFixed(2)}` : '',
      descripcion: p.descripcion || '',
      imagen_url: p.imagen_url
    });
  }
});

mensajeCompleto += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n`;
mensajeCompleto += `üõí *¬øDeseas hacer un pedido?*\n\n`;
mensajeCompleto += `üìã Escribe el n√∫mero y cantidad:\n`;
mensajeCompleto += `üìå Ejemplo: *1,2* (2 unidades del producto 1)\n\n`;
mensajeCompleto += `Para varios productos:\n`;
mensajeCompleto += `üìå Ejemplo: *1,2;3,1* (2 del prod.1 y 1 del prod.3)\n\n`;
mensajeCompleto += `‚úèÔ∏è Escribe tu pedido ahora o *cancelar* para salir.`;

return {
  numeroOrigen: switchData.numeroOrigen,
  empresaId: switchData.empresaId,
  nombreSesion: nombreSesion,
  pushName: switchData.pushName,
  mensaje: mensajeCompleto,
  productosConImagen: productosConImagen
};

const switchData = $('Switch').first().json;
const productosResponse = $input.first().json;
const productos = productosResponse.data;

let nombreSesion = switchData.nombreSesion;
if (nombreSesion && nombreSesion.startsWith(`${switchData.empresaId}_`)) {
  nombreSesion = nombreSesion.substring(`${switchData.empresaId}_`.length);
}

const pedidoStr = switchData.mensaje_usuario;

if (!pedidoStr || pedidoStr.trim() === '') {
  return {
    numeroOrigen: switchData.numeroOrigen,
    empresaId: switchData.empresaId,
    nombreSesion: nombreSesion,
    pushName: switchData.pushName,
    mensaje: "‚ö†Ô∏è *Formato incorrecto*\n\nEscribe el n√∫mero del producto y la cantidad.\n\nüìå Ejemplo: *1,2* (producto 1, cantidad 2)",
    tipo: 'error_formato'
  };
}

const pares = pedidoStr.split(';').map(p => p.trim()).filter(p => p);
const items = [];
let total = 0;
let errorMsg = '';

for (const par of pares) {
  const partes = par.split(',').map(p => p.trim());

  if (partes.length !== 2) {
    errorMsg = `‚ö†Ô∏è *Formato inv√°lido*\n\nEl formato correcto es:\nüìå *1,2* (producto 1, cantidad 2)`;
    break;
  }

  const [idStr, cantStr] = partes;
  const id = parseInt(idStr);
  const cantidad = parseInt(cantStr);

  if (isNaN(id) || isNaN(cantidad) || cantidad <= 0 || id <= 0) {
    errorMsg = `‚ö†Ô∏è *Formato inv√°lido*\n\nEl formato correcto es:\nüìå *1,2* (producto 1, cantidad 2)`;
    break;
  }

  const producto = productos.find(p => p.id === id);

  if (!producto) {
    errorMsg = `‚ö†Ô∏è *Producto no encontrado*\n\nEl producto #${id} no existe.\n\nüìã Escribe *productos* para ver el cat√°logo.`;
    break;
  }

  if (!producto.precio || isNaN(parseFloat(producto.precio))) {
    errorMsg = `‚ö†Ô∏è *Error en producto #${id}*\n\nEl producto no tiene precio v√°lido.`;
    break;
  }

  const subtotal = parseFloat(producto.precio) * cantidad;
  total += subtotal;

  items.push({
    producto_id: producto.id,
    nombre: producto.nombre_item,
    cantidad: cantidad,
    precio_unitario: parseFloat(producto.precio),
    subtotal: subtotal
  });
}

if (errorMsg) {
  return {
    numeroOrigen: switchData.numeroOrigen,
    empresaId: switchData.empresaId,
    nombreSesion: nombreSesion,
    pushName: switchData.pushName,
    mensaje: errorMsg,
    tipo: 'error_pedido'
  };
}

let resumen = 'üõí *Resumen de tu pedido:*\n\n';
items.forEach((item, i) => {
  resumen += `${i + 1}. ${item.nombre}\n`;
  resumen += `   üí∞ $${item.precio_unitario.toFixed(2)} √ó ${item.cantidad} = $${item.subtotal.toFixed(2)}\n\n`;
});
resumen += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
resumen += `üíµ *Total: $${total.toFixed(2)}*\n\n`;
resumen += `üìù Por favor, escribe tu *nombre completo* para confirmar el pedido.\n\n`;
resumen += `‚ùå O escribe *cancelar* para anular.`;

return {
  numeroOrigen: switchData.numeroOrigen,
  empresaId: switchData.empresaId,
  nombreSesion: nombreSesion,
  pushName: switchData.pushName,
  mensaje: resumen,
  tipo: 'pedir_nombre',
  pedido_temporal: {
    items: items,
    total: total,
    estado: 'esperando_nombre',
    fecha_creacion: new Date().toISOString()
  }
};

const pedidoResponse = $input.first().json;
const switchData = $('Switch').first().json;

let mensaje;

if (pedidoResponse.success) {
  const pedido = pedidoResponse.data;

  mensaje = `‚úÖ *¬°Pedido confirmado!*

üë§ Cliente: ${switchData.nombreCliente}
üî¢ Pedido #${pedido.id}

üì¶ *Resumen:*
`;

  switchData.items.forEach((item, i) => {
    mensaje += `${i + 1}. ${item.nombre} x${item.cantidad}
`;
  });

  mensaje += `
üíµ *Total: ${switchData.total.toFixed(2)}*

üìû Te contactaremos pronto.

¬°Gracias por tu compra!`;

  try {
    await fetch(`http://host.docker.internal:3000/api/pedidos/sesion/${switchData.empresaId}/${switchData.numeroOrigen}`, {
      method: 'DELETE'
    });
    console.log('‚úÖ Sesi√≥n limpiada');
  } catch (error) {
    console.log('‚ö†Ô∏è Error limpiando sesi√≥n:', error.message);
  }

} else {
  mensaje = `‚ùå *Error al crear el pedido*

Por favor intenta de nuevo o contacta con soporte.`;
}

return {
  numeroOrigen: switchData.numeroOrigen,
  empresaId: switchData.empresaId,
  nombreSesion: switchData.nombreSesion,
  mensaje: mensaje
};

const codeData = $('Code in JavaScript').first().json;
const webhookData = $('Webhook').first().json.body;
const nombreUsuario = webhookData.pushName || 'amigo';

let nombreSesion = codeData.nombreSesion;
if (nombreSesion.startsWith(`${codeData.empresaId}_`)) {
