const procesarData = $input.first().json;
const switchData = $('Switch').first().json;

let nombreSesion = switchData.nombreSesion;
if (nombreSesion && nombreSesion.startsWith(`${switchData.empresaId}_`)) {
  nombreSesion = nombreSesion.substring(`${switchData.empresaId}_`.length);
}

return {
  json: {
    numeroOrigen: switchData.numeroOrigen,
    empresaId: switchData.empresaId,
    nombreSesion: nombreSesion,
    pushName: switchData.pushName,
    mensaje: procesarData.mensaje,
    pedido_temporal: procesarData.pedido_temporal,
    tipo: procesarData.tipo,
    abierto: switchData.abierto
  }
};

const tipo = $input.item.json.tipo;

const tiposParaGuardar = ['pedir_catalogo', 'agregar_producto', 'pedir_nombre'];

if (tiposParaGuardar.includes(tipo)) {
  return $input.item;
} else {
  return null;
}

const config = $('HTTP Request').first().json;
const switchData = $('Switch').first().json;

let nombreSesion = switchData.nombreSesion;
if (nombreSesion && nombreSesion.startsWith(`${switchData.empresaId}_`)) {
  nombreSesion = nombreSesion.substring(`${switchData.empresaId}_`.length);
}

const horaInicio = config.hora_inicio || 9;
const horaFin = config.hora_fin || 18;
const diasLaborales = config.dias_laborales || [1, 2, 3, 4, 5];

const nombresDias = {
  0: 'Domingo',
  1: 'Lunes',
  2: 'Martes',
