const config = $('HTTP Request').first().json;
const formatearData = $('Formatear pedido').first().json;

let nombreSesion = formatearData.nombreSesion;
if (nombreSesion && nombreSesion.startsWith(`${formatearData.empresaId}_`)) {
  nombreSesion = nombreSesion.substring(`${formatearData.empresaId}_`.length);
}

const horaInicio = config.hora_inicio || 9;
