const horaLocal = new Date(now.toLocaleString('en-US', { timeZone: ZONA_HORARIA }));
const hour = horaLocal.getHours();
const day = horaLocal.getDay();

const horaInicio = config.horario_inicio 
  ? parseInt(config.horario_inicio.split(':')[0]) 
  : (config.hora_inicio || 9);

const horaFin = config.horario_fin 
  ? parseInt(config.horario_fin.split(':')[0]) 
  : (config.hora_fin || 18);

let diasLaborales = [1, 2, 3, 4, 5];
if (config.dias_laborales) {
  try {
    diasLaborales = typeof config.dias_laborales === 'string' 
      ? JSON.parse(config.dias_laborales) 
      : config.dias_laborales;
  } catch (e) {
    console.log('âš ï¸ Error parseando dias_laborales:', e.message);
  }
}

const enDiaLaboral = diasLaborales.includes(day);
const enHorario = hour >= horaInicio && hour < horaFin;
const abierto = enDiaLaboral && enHorario;

